import XCTest
@testable import DadKit

class ItemTests: XCTestCase {

    override func setUp() {
        Bungie.key = ENV_API_KEY
        Bungie.appId = ""
        Bungie.appVersion = ""
    }

    func test_DecodeWeaponResponse() {
        let data = plaintextWeaponResponseExample.data(using: .utf8)!

        do {
            _ = try JSONDecoder().decode(MetaItemResponse.self, from: data)
        } catch {
            XCTFail(error.localizedDescription)
        }
    }

    func test_DecodeArmorResponse() {
        let data = plaintextArmorResponseExample.data(using: .utf8)!

        do {
            _ = try JSONDecoder().decode(MetaItemResponse.self, from: data)
        } catch {
            XCTFail(error.localizedDescription)
        }
    }

    func test_GetItemRequestResponds200() {
        let req = Bungie.API.getItem(withId: "2712244741").request //Bygones
        let x = expectation(description: "Get Item request responds with 200.")
        let promise = URLSession.shared.dataTask(.promise, with: req).validate()

        _ = promise.done { _ in
            x.fulfill()
        }.catch {
            XCTFail($0.localizedDescription)
            x.fulfill()
        }

        wait(for: [x], timeout: 10)
    }
    
}

